      mode: 'lines',
      line: {color: '#ef4444', width: 2}
    },
    {
      x: data.map(d =&gt; d.sl),
      y: data.map(d =&gt; d.shortage),
      name: 'Costo Escasez',
      type: 'scatter',
      mode: 'lines',
      line: {color: '#f97316', width: 2}
    },
    {
      x: data.map(d =&gt; d.sl),
      y: data.map(d =&gt; d.total),
      name: 'Costo Total',
      type: 'scatter',
      mode: 'lines',
      line: {color: '#2563eb', width: 4}
    }
  ];

  const layout = {
    title: 'Optimización del Nivel de Servicio',
    xaxis: { 
      title: 'Nivel de Servicio (%)', 
      gridcolor: '#f1f5f9',
      range: [50, 100]
    },
    yaxis: { title: 'Costo ($)', gridcolor: '#f1f5f9' },
    template: 'plotly_white',
    hovermode: 'x unified',
    margin: { t: 50, b: 50, l: 60, r: 30 },
    annotations: [{
      x: optimal.sl,
      y: optimal.total,
      text: `Óptimo: ${optimal.sl.toFixed(1)}%&lt;br&gt;$${Math.round(optimal.total)}`,
      showarrow: true,
      arrowhead: 2,
