    name: 'Demanda Real (Historia)',
    type: 'scatter',
    mode: 'lines',
    line: {color: '#475569', width: 3}
  };

  const trace_naive = {
    x: forecasts.map(d =&gt; d.t),
    y: forecasts.map(d =&gt; d.naive),
    name: 'Naive (Inercia)',
    line: {color: '#ef4444', dash: 'dash', width: 2}
  };

  const trace_snaive = {
    x: forecasts.map(d =&gt; d.t),
    y: forecasts.map(d =&gt; d.snaive),
    name: 'S-Naive (Ciclo)',
    line: {color: '#3b82f6', dash: 'dot', width: 2}
  };

  const trace_drift = {
    x: forecasts.map(d =&gt; d.t),
    y: forecasts.map(d =&gt; d.drift),
    name: 'Drift (Trayectoria)',
